import { useState, useEffect } from "react";
import { getAuth } from "firebase/auth";
import { getFirestore, doc, getDoc } from "firebase/firestore";

const DisplayHighScore = () => {
  const [highScore, setHighScore] = useState(null); // State to store highscore

  useEffect(() => {
    const fetchHighScore = async () => {
      const auth = getAuth();
      const db = getFirestore();
      const user = auth.currentUser; // Get current authenticated user
      if (user) {
        const userRef = doc(db, "users", user.uid); // Reference to the user's document
        const docSnap = await getDoc(userRef); // Fetch user document

        if (docSnap.exists()) {
          // Get highscore from the document data
          const userHighScore = docSnap.data().highscore || 0;
          setHighScore(userHighScore); // Set highscore in state
        } else {
          console.log("No user data found");
        }
      }
    };

    fetchHighScore();
  }, []); // Empty dependency array to run once when component mounts

  return (
    <div>
      <h2>High Score: {highScore !== null ? highScore : "Loading..."}</h2>
    </div>
  );
};

export default DisplayHighScore;
