import { useEffect, useRef, useState } from "react";

export default function PiggleGame() {
  const canvasRef = useRef(null);
  const ballRef = useRef({
    x: 200, y: 50, dx: 0, dy: 0, radius: 10, launched: false
  });
  const [cannonAngle, setCannonAngle] = useState(0);
  const pegs = useRef([
    { x: 150, y: 300, radius: 10, hit: false },
    { x: 250, y: 350, radius: 10, hit: false },
    { x: 200, y: 400, radius: 10, hit: false }
  ]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    let animationFrameId;

    const drawCannon = () => {
      ctx.save();
      ctx.translate(200, 50);
      ctx.rotate(cannonAngle);
      ctx.fillStyle = "black";
      ctx.fillRect(-10, -10, 30, 20);
      ctx.restore();
    };

    const drawBall = () => {
      ctx.beginPath();
      ctx.arc(ballRef.current.x, ballRef.current.y, ballRef.current.radius, 0, Math.PI * 2);
      ctx.fillStyle = "red";
      ctx.fill();
      ctx.closePath();
    };

    const drawPegs = () => {
      pegs.current.forEach(peg => {
        if (!peg.hit) {
          ctx.beginPath();
          ctx.arc(peg.x, peg.y, peg.radius, 0, Math.PI * 2);
          ctx.fillStyle = "blue";
          ctx.fill();
          ctx.closePath();
        }
      });
    };

    const handleCollisions = () => {
      pegs.current.forEach(peg => {
        if (peg.hit) return;

        const dx = ballRef.current.x - peg.x;
        const dy = ballRef.current.y - peg.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < ballRef.current.radius + peg.radius) {
          // Bounce calculation
          const normalX = dx / distance;
          const normalY = dy / distance;
          const dotProduct = ballRef.current.dx * normalX + ballRef.current.dy * normalY;
          ballRef.current.dx -= 2 * dotProduct * normalX;
          ballRef.current.dy -= 2 * dotProduct * normalY;

          // Mark peg as hit
          peg.hit = true;
        }
      });
    };

    const updateBall = () => {
      let ball = ballRef.current;
      if (ball.launched) {
        ball.dy += 0.1; // Gravity
        ball.x += ball.dx;
        ball.y += ball.dy;

        // Bounce off walls
        if (ball.x - ball.radius < 0 || ball.x + ball.radius > canvas.width) {
          ball.dx *= -1;
        }

        // Bounce off floor
        if (ball.y + ball.radius > canvas.height) {
          ball.dy *= -0.8;
          ball.y = canvas.height - ball.radius;
        }

        handleCollisions();
      }
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      drawCannon();
      drawPegs();
      drawBall();
      updateBall();
      animationFrameId = requestAnimationFrame(animate);
    };

    animate();
    return () => cancelAnimationFrame(animationFrameId);
  }, [cannonAngle]);

  const launchBall = () => {
    if (!ballRef.current.launched) {
      ballRef.current.dx = Math.cos(cannonAngle) * 5;
      ballRef.current.dy = Math.sin(cannonAngle) * 5;
      ballRef.current.launched = true;
    }
  };

  const handleMouseMove = (event) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const mouseX = event.clientX - rect.left;
    const mouseY = event.clientY - rect.top;
    setCannonAngle(Math.atan2(mouseY - 50, mouseX - 200));
  };

  return (
    <div>
      <canvas ref={canvasRef} width={400} height={500} className="border"
        onClick={launchBall} onMouseMove={handleMouseMove} />
    </div>
  );
}
