/**
 * Piggle is a game where you hit pegs with balls
 */
import { useEffect, useRef, useState } from "react";

/**
 * Main game runner
 * @returns The canvas which the game is run in
 */
export default function PiggleGame() {
    // Reference to canvas element
    const canvasRef = useRef(null);

    // Constants for game mechanics gravity and max shots
    const BALL_GRAVITY = 0.03;
    const MAX_SHOTS = 10;
    const NUMBER_OF_PEGS = 20;

    // Ball state, including position, velocity, and launch status
    const ballRef = useRef({
        x: 200, y: 50, dx: 0, dy: 0, radius: 10, launched: false
    });

    // State for cannon angle
    const [cannonAngle, setCannonAngle] = useState(0);
    // State for remaining shots
    const [shotsLeft, setShotsLeft] = useState(MAX_SHOTS);
    const [gameMessage, setGameMessage] = useState("");

    // Generate pegs at random positions
    const generatePegsRandomly = () => (
        // Generate n pegs
        Array.from({ length: NUMBER_OF_PEGS }, () => ({
            x: Math.random() * 380 + 10,
            y: Math.random() * 300 + 100,
            radius: 10,
            hit: false
        }))
    );

    // Generate pegs in a square grid
    const generatePegsGrid = () => {
        const rows = 5;
        const cols = 5;
        // Horizontal spacing between pegs
        const spacingX = 60;
        // Vertical spacing between pegs
        const spacingY = 60;
        const startX = 50;  // Offset to center pegs
        const startY = 100;

        let pegs = [];
        for (let row = 0; row < rows; row++) {
            for (let col = 0; col < cols; col++) {
                pegs.push({
                    x: startX + col * spacingX,
                    y: startY + row * spacingY,
                    radius: 10,
                    hit: false
                });
            }
        }
        return pegs;
    };

    // Generate pegs in a circular pattern
    const generatePegsCircular = (centerX, centerY, radius, numPegs) => {

        const angleIncrement = (2 * Math.PI) / numPegs;
        let pegs = [];

        for (let i = 0; i < NUMBER_OF_PEGS; i++) {
            const angle = angleIncrement * i;
            pegs.push({
                x: centerX + radius * Math.cos(angle),
                y: centerY + radius * Math.sin(angle),
                radius: 10,
                hit: false
            });
        }
        return pegs;
    };

    // Generate pegs in a hexagonal pattern
    const generatePegsHexagonal = () => {
        let pegs = [];
        const rows = 10;
        const cols = 10;
        const spacing = 10;

        for (let row = 0; row < rows; row++) {
            for (let col = 0; col < cols; col++) {
                // Offset every other row for the hexagonal arrangement
                const offsetX = (row % 2 === 0) ? 0 : spacing / 2;
                pegs.push({
                    x: col * spacing + offsetX,
                    y: row * spacing * Math.sqrt(3) / 2,
                    radius: 10,
                    hit: false
                });
            }
        }
        return pegs;
    };

    // Generate pegs in a triangular pattern
    const generatePegsTriangular = (rows) => {
        let pegs = [];

        for (let row = 0; row < rows; row++) {
            for (let col = 0; col <= row; col++) {
                const x = 50 + col * 40;
                const y = 100 + row * 40;
                pegs.push({
                    x: x,
                    y: y,
                    radius: 10,
                    hit: false
                });
            }
        }
        return pegs;
    };


    // Reference to pegs in the game
    const pegs = useRef(generatePegsGrid());

    // Game loop and rendering logic
    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");
        let animationFrameId;

        // Draw cannon at current angle
        const drawCannon = () => {
            ctx.save();
            ctx.translate(200, 50);
            ctx.rotate(cannonAngle);
            ctx.fillStyle = "black";
            ctx.fillRect(-10, -10, 30, 20);
            ctx.restore();
        };

        // Draw the ball at its current position
        const drawBall = () => {
            ctx.beginPath();
            ctx.arc(ballRef.current.x, ballRef.current.y, ballRef.current.radius, 0, Math.PI * 2);
            ctx.fillStyle = "purple";
            ctx.fill();
            ctx.closePath();
        };

        // Draw pegs, ignoring ones that have been hit
        const drawPegs = () => {
            pegs.current.forEach(peg => {
                if (!peg.hit) {
                    ctx.beginPath();
                    ctx.arc(peg.x, peg.y, peg.radius, 0, Math.PI * 2);
                    ctx.fillStyle = "blue";
                    ctx.fill();
                    ctx.closePath();
                }
            });
        };

        // Handle ball collision with pegs
        const handleCollisions = () => {
            pegs.current.forEach(peg => {
                if (peg.hit) return;
                const dx = ballRef.current.x - peg.x;
                const dy = ballRef.current.y - peg.y;
                const distance = Math.sqrt(dx * dx + dy * dy);

                if (distance < ballRef.current.radius + peg.radius) {
                    const normalX = dx / distance;
                    const normalY = dy / distance;
                    const dotProduct = ballRef.current.dx * normalX + ballRef.current.dy * normalY;
                    ballRef.current.dx -= 2 * dotProduct * normalX;
                    ballRef.current.dy -= 2 * dotProduct * normalY;
                    peg.hit = true;
                }
            });
        };

        // Update ball movement with gravity and collision checks
        const updateBall = () => {
            let ball = ballRef.current;
            if (ball.launched) {
                ball.dy += BALL_GRAVITY;
                ball.x += ball.dx;
                ball.y += ball.dy;

                // Bounce off walls
                if (ball.x - ball.radius < 0 || ball.x + ball.radius > canvas.width || ball.y + ball.radius <= 0) {
                    ball.dx *= -1;
                }
                // If the ball hits the ground, reset position and check game status
                if (ball.y + ball.radius > canvas.height) {
                    ball.launched = false;
                    ball.x = 200;
                    ball.y = 50;
                    ball.dx = 0;
                    ball.dy = 0;
                    checkGameStatus();
                }
                handleCollisions();
            }
        };

        // Check if the game is won or lost
        const checkGameStatus = () => {
            if (pegs.current.every(peg => peg.hit)) {
                setGameMessage("You Win!");
            } else if (shotsLeft <= 0) {
                setGameMessage("You Lose!");
            }
        };

        // Main game loop, updates and redraws elements
        const animate = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            drawCannon();
            drawPegs();
            drawBall();
            updateBall();
            animationFrameId = requestAnimationFrame(animate);
        };

        animate();
        return () => cancelAnimationFrame(animationFrameId);
    }, [cannonAngle, shotsLeft]);

    // Launch the ball when clicking the canvas
    const launchBall = () => {
        if (!ballRef.current.launched && shotsLeft > 0 && gameMessage === "") {
            ballRef.current.dx = Math.cos(cannonAngle) * 3;
            ballRef.current.dy = Math.sin(cannonAngle) * 2;
            ballRef.current.launched = true;
            setShotsLeft(shotsLeft - 1);
        }
    };

    // Adjust cannon angle based on mouse position
    const handleMouseMove = (event) => {
        const canvas = canvasRef.current;
        const rect = canvas.getBoundingClientRect();
        const mouseX = event.clientX - rect.left;
        const mouseY = event.clientY - rect.top;
        setCannonAngle(Math.atan2(mouseY - 50, mouseX - 200));
    };

    return (
        <div style={{ textAlign: "center" }}>
            {/* Display shots left */}
            <p>Shots Left: {shotsLeft}</p>
            {/* Show win/lose message if applicable */}
            {gameMessage && <h2>{gameMessage}</h2>}
            <canvas
                ref={canvasRef}
                width={400}
                height={500}
                className="border"
                onClick={launchBall}
                onMouseMove={handleMouseMove}
            />
        </div>
    );
}
