import { useEffect, useRef, useState } from "react";

export default function PiggleGame() {
  const canvasRef = useRef(null);
  const ballRef = useRef({ x: 200, y: 400, dx: 0, dy: 0, radius: 10, launched: false });
  const pegs = useRef([
    { x: 150, y: 300, radius: 10 },
    { x: 250, y: 350, radius: 10 },
  ]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    let animationFrameId;

    const drawBall = () => {
      ctx.beginPath();
      ctx.arc(ballRef.current.x, ballRef.current.y, ballRef.current.radius, 0, Math.PI * 2);
      ctx.fillStyle = "red";
      ctx.fill();
      ctx.closePath();
    };

    const drawPegs = () => {
      pegs.current.forEach(peg => {
        ctx.beginPath();
        ctx.arc(peg.x, peg.y, peg.radius, 0, Math.PI * 2);
        ctx.fillStyle = "blue";
        ctx.fill();
        ctx.closePath();
      });
    };

    const detectCollisions = () => {
      pegs.current = pegs.current.filter(peg => {
        const dx = ballRef.current.x - peg.x;
        const dy = ballRef.current.y - peg.y;
        const distance = Math.sqrt(dx * dx + dy * dy);
        return distance > ballRef.current.radius + peg.radius;
      });
    };

    const updateBall = () => {
      let ball = ballRef.current;
      if (ball.launched) {
        ball.dy += 0.1; // Gravity
        ball.x += ball.dx;
        ball.y += ball.dy;

        // Bounce off walls
        if (ball.x - ball.radius < 0 || ball.x + ball.radius > canvas.width) {
          ball.dx *= -1;
        }

        // Bounce off floor
        if (ball.y + ball.radius > canvas.height) {
          ball.dy *= -0.8; // Energy loss
          ball.y = canvas.height - ball.radius;
        }

        detectCollisions();
      }
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      drawPegs();
      drawBall();
      updateBall();
      animationFrameId = requestAnimationFrame(animate);
    };

    animate();
    return () => cancelAnimationFrame(animationFrameId);
  }, []);

  const launchBall = () => {
    if (!ballRef.current.launched) {
      ballRef.current.dx = (Math.random() - 0.5) * 5; // Random launch angle
      ballRef.current.dy = -5; // Launch upwards
      ballRef.current.launched = true;
    }
  };

  return (
    <div>
      <canvas ref={canvasRef} width={400} height={500} className="border" onClick={launchBall} />
    </div>
  );
}
