import React, { useState } from "react";
import "./styles/HowToPlay.css"; // Import the CSS file

const HowToPlay = () => {
    const [isOpen, setIsOpen] = useState(true); // Show pop-up on page load

    return (
        <>
            {/* Pop-up Modal (covers the whole screen) */}
            {isOpen && (
                <div className="overlay">
                    <div className="popup">
                        <h2>How to Play</h2>
                        <p>Aim with your mouse and click to shoot the ball.</p>
                        <p>Try to get the highest score possible!</p>
                        <button onClick={() => setIsOpen(false)} className="close-btn">
                            Close
                        </button>
                    </div>
                </div>
            )}
        </>
    );
};

export default HowToPlay;
