import { useEffect, useRef, useState } from "react";

// This is the main game component, PiggleGame
export default function PiggleGame() {
  // canvasRef is used to access the canvas element
  const canvasRef = useRef(null);

  // ballRef holds the state of the ball: position, velocity (dx, dy), radius, and launch status
  const ballRef = useRef({
    x: 200,
    y: 50,
    dx: 0,
    dy: 0,
    radius: 10,
    launched: false
  });

  // cannonAngle stores the current angle of the cannon
  const [cannonAngle, setCannonAngle] = useState(0);

  // generatePegs creates a list of pegs with random positions
  const generatePegs = () => (
    Array.from({ length: 10 }, () => ({
      x: Math.random() * 380 + 10, // Random x position within canvas width
      y: Math.random() * 300 + 100, // Random y position within the lower part of the canvas
      radius: 10, // Size of the pegs
      hit: false // Track if the peg has been hit
    }))
  );

  // pegs holds the current state of all pegs in the game
  const pegs = useRef(generatePegs());

  // useEffect runs the game loop and handles rendering
  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    let animationFrameId;

    // drawCannon draws the cannon at the current angle
    const drawCannon = () => {
      ctx.save();
      ctx.translate(200, 50); // Position the cannon at (200, 50)
      ctx.rotate(cannonAngle); // Rotate the cannon by the current cannonAngle
      ctx.fillStyle = "black"; // Set cannon color
      ctx.fillRect(-10, -10, 30, 20); // Draw the cannon (a black rectangle)
      ctx.restore();
    };

    // drawBall draws the ball at its current position
    const drawBall = () => {
      ctx.beginPath(); // Start a new drawing path
      ctx.arc(ballRef.current.x, ballRef.current.y, ballRef.current.radius, 0, Math.PI * 2); // Draw a circle (ball)
      ctx.fillStyle = "red"; // Set ball color to red
      ctx.fill(); // Fill the circle with color
      ctx.closePath(); // Close the drawing path
    };

    // drawPegs draws each peg on the canvas, skipping those that have been hit
    const drawPegs = () => {
      pegs.current.forEach(peg => {
        if (!peg.hit) {
          ctx.beginPath();
          ctx.arc(peg.x, peg.y, peg.radius, 0, Math.PI * 2); // Draw each peg as a circle
          ctx.fillStyle = "blue"; // Set peg color to blue
          ctx.fill();
          ctx.closePath();
        }
      });
    };

    // handleCollisions checks if the ball has collided with any pegs
    const handleCollisions = () => {
      pegs.current.forEach(peg => {
        if (peg.hit) return; // Skip if the peg has already been hit

        const dx = ballRef.current.x - peg.x; // Horizontal distance between ball and peg
        const dy = ballRef.current.y - peg.y; // Vertical distance between ball and peg
        const distance = Math.sqrt(dx * dx + dy * dy); // Calculate distance between ball and peg

        // If the ball's radius plus the peg's radius is larger than the distance, a collision occurred
        if (distance < ballRef.current.radius + peg.radius) {
          const normalX = dx / distance; // Normal vector (x-component)
          const normalY = dy / distance; // Normal vector (y-component)
          const dotProduct = ballRef.current.dx * normalX + ballRef.current.dy * normalY; // Dot product of ball's velocity and normal vector

          // Reflect the ball's velocity off the peg by adjusting its velocity (bounce effect)
          ballRef.current.dx -= 2 * dotProduct * normalX;
          ballRef.current.dy -= 2 * dotProduct * normalY;
          peg.hit = true; // Mark peg as hit
        }
      });
    };

    // updateBall updates the position of the ball based on its velocity
    const updateBall = () => {
      let ball = ballRef.current;
      if (ball.launched) {
        ball.dy += 0.1; // Gravity effect (accelerates the ball downward)
        ball.x += ball.dx; // Update ball's x-position based on horizontal velocity
        ball.y += ball.dy; // Update ball's y-position based on vertical velocity

        // Check for wall collisions and reverse direction if needed
        if (ball.x - ball.radius < 0 || ball.x + ball.radius > canvas.width) {
          ball.dx *= -1; // Reverse horizontal velocity on wall hit
        }
        if (ball.y + ball.radius > canvas.height) { // Ball hit the ground
          ball.launched = false; // Stop the ball from moving
          ball.x = 200; // Reset ball position
          ball.y = 50;
          ball.dx = 0; // Reset velocity
          ball.dy = 0;
        }
        handleCollisions(); // Check for peg collisions
      }
    };

    // animate is the main game loop that continuously updates the canvas
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear the canvas before drawing the next frame
      drawCannon(); // Draw the cannon
      drawPegs(); // Draw the pegs
      drawBall(); // Draw the ball
      updateBall(); // Update ball position and handle gravity/collisions
      animationFrameId = requestAnimationFrame(animate); // Request the next animation frame
    };

    animate(); // Start the game loop
    return () => cancelAnimationFrame(animationFrameId); // Cleanup on component unmount
  }, [cannonAngle]); // Re-run useEffect when cannonAngle changes

  // launchBall starts the ball's movement when clicked
  const launchBall = () => {
    if (!ballRef.current.launched) {
      // Set ball's initial velocity based on cannon angle
      ballRef.current.dx = Math.cos(cannonAngle) * 5;
      ballRef.current.dy = Math.sin(cannonAngle) * 5;
      ballRef.current.launched = true; // Mark ball as launched
    }
  };

  // handleMouseMove updates the cannon angle based on mouse position
  const handleMouseMove = (event) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect(); // Get the position of the canvas on screen
    const mouseX = event.clientX - rect.left; // Calculate mouse X relative to canvas
    const mouseY = event.clientY - rect.top; // Calculate mouse Y relative to canvas
    setCannonAngle(Math.atan2(mouseY - 50, mouseX - 200)); // Update the cannon angle based on mouse position
  };

  return (
    <div>
      <canvas
        ref={canvasRef} // Attach canvas reference
        width={400}
        height={500}
        className="border"
        onClick={launchBall} // Launch ball on canvas click
        onMouseMove={handleMouseMove} // Update cannon angle on mouse movement
      />
    </div>
  );
}
