import React, { useState } from "react";

const HowToPlay = () => {
    const [isOpen, setIsOpen] = useState(false);

    return (
        <>
            {/* Button to open the pop-up */}
            <button
                onClick={() => setIsOpen(true)}
                className="fixed bottom-5 left-5 bg-blue-500 text-white px-4 py-2 rounded-lg shadow-lg hover:bg-blue-600"
            >
                How to Play
            </button>

            {/* Pop-up Modal */}
            {isOpen && (
                <div className="fixed inset-0 flex justify-center items-center bg-black bg-opacity-50">
                    <div className="bg-white p-6 rounded-lg shadow-lg max-w-md">
                        <h2 className="text-2xl font-bold mb-4">How to Play</h2>
                        <p>Aim with your mouse and click to shoot the ball</p>
                        <p>Hit all of the red pegs to win</p>
                        <p>Try to get the highest possible score!</p>
                        <button
                            onClick={() => setIsOpen(false)}
                            className="mt-4 bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600"
                        >
                            Close
                        </button>
                    </div>
                </div>
            )}
        </>
    );
};

export default HowToPlay;
