import { useState, useEffect } from "react";
import { getAuth } from "firebase/auth";
import { getFirestore, collection, query, orderBy, limit, getDocs } from "firebase/firestore";

const TopScore = () => {
    const [topScore, setTopScore] = useState(null);
    const [topScoreUser, setTopScoreUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchTopScore = async () => {
            const db = getFirestore();

            // Query to get the highest score across all users
            const usersRef = collection(db, "users");
            const q = query(usersRef, orderBy("highscore", "desc"), limit(1));

            try {
                const querySnapshot = await getDocs(q);
                if (!querySnapshot.empty) {
                    const topUser = querySnapshot.docs[0].data();
                    setTopScore(topUser.highscore);
                    setTopScoreUser(topUser.username);
                } else {
                    console.log("No users found.");
                }
            } catch (error) {
                console.error("Error fetching top score:", error);
            }
            setLoading(false);
        };

        fetchTopScore();
    }, []);

    if (loading) return <div>Loading...</div>;

    return (
        <div>
            {topScore !== null ? (
                <h2>Global Top Score: {topScore} held by: {topScoreUser}</h2>
            ) : (
                <p>No top score available</p>
            )}
        </div>
    );
};

export default TopScore;
