import { useEffect, useRef, useState } from "react";

export default function PiggleGame() {
  const canvasRef = useRef(null);
  const [ball, setBall] = useState({ x: 200, y: 100, dx: 2, dy: 2, radius: 10 });
  const [pegs, setPegs] = useState([
    { x: 150, y: 300, radius: 10 },
    { x: 250, y: 350, radius: 10 },
  ]);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    let animationFrameId;

    const drawBall = () => {
      ctx.beginPath();
      ctx.arc(ball.x, ball.y, ball.radius, 0, Math.PI * 2);
      ctx.fillStyle = "red";
      ctx.fill();
      ctx.closePath();
    };

    const drawPegs = () => {
      pegs.forEach(peg => {
        ctx.beginPath();
        ctx.arc(peg.x, peg.y, peg.radius, 0, Math.PI * 2);
        ctx.fillStyle = "blue";
        ctx.fill();
        ctx.closePath();
      });
    };

    const updateBall = () => {
      setBall(prev => {
        let newDx = prev.dx;
        let newDy = prev.dy + 0.1; // Gravity

        let newX = prev.x + newDx;
        let newY = prev.y + newDy;

        // Bounce off walls
        if (newX - prev.radius < 0 || newX + prev.radius > canvas.width) {
          newDx = -newDx;
        }

        if (newY + prev.radius > canvas.height) {
          newDy = -newDy * 0.8; // Some energy loss
        }

        return { ...prev, x: newX, y: newY, dx: newDx, dy: newDy };
      });
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      drawPegs();
      drawBall();
      updateBall();
      animationFrameId = requestAnimationFrame(animate);
    };

    animate();
    return () => cancelAnimationFrame(animationFrameId);
  }, [ball, pegs]);

  return <canvas ref={canvasRef} width={400} height={500} className="border" />;
}
