let NUMBER_OF_PEGS = 20;
const PEG_RADIUS = 10;

/**
 * This method will generate level 1
 */
const LevelOne = () => {
    let redPegCount = 0;
    let bluePegCount = 0;
    // Rows and column number
    const rows = 5;
    const cols = 5;
    // Peg spacing
    const spacing = 50;
    // Starting position for the top left peg
    const startX = 100;
    const startY = 170;

    // Generate the pegs
    let pegs = [];
    for (let row = 0; row < rows; row++) {
        for (let col = 0; col < cols; col++) {
            let pegType = "blue";
            // Ensure red pegs don't exceed blue pegs
            if (Math.random() < 0.3 && redPegCount < bluePegCount) {
                pegType = "red";
                redPegCount++;
            } else {
                bluePegCount++;
            }
            pegs.push({
                x: startX + col * spacing,
                y: startY + row * spacing,
                radius: PEG_RADIUS,
                hit: false,
                type: pegType
            });
        }
    }
    return pegs;
};

/**
 * This method will generate the pegs randomly
 * @returns The pegs generated in a random
 */
const LevelTwo = () => {
    let pegs = [];
    let redPegCount = 0;
    let bluePegCount = 0;
    const totalPegs = NUMBER_OF_PEGS;

    for (let i = 0; i < totalPegs; i++) {
        let pegType = "blue";

        // Ensure red pegs don't exceed blue pegs
        if (Math.random() < 0.3 && redPegCount < bluePegCount) {
            pegType = "red";
            redPegCount++;
        } else {
            bluePegCount++;
        }

        pegs.push({
            x: (Math.random() * 380) + 10,
            y: (Math.random() * 300) + 100,
            radius: PEG_RADIUS,
            hit: false,
            type: pegType
        });
    };

    return pegs;
}

/**
 * This method will generate the pegs randomly
 * @returns The pegs generated in a random
 */
const LevelThree = () => {
    let pegs = [];
    let redPegCount = 0;
    let bluePegCount = 0;
    const totalPegs = NUMBER_OF_PEGS;

    for (let i = 0; i < totalPegs; i++) {
        let pegType = "blue";

        // Ensure red pegs don't exceed blue pegs
        if (Math.random() < 0.3 && redPegCount < bluePegCount) {
            pegType = "red";
            redPegCount++;
        } else {
            bluePegCount++;
        }

        pegs.push({
            x: (Math.random() * 380) + 10,
            y: (Math.random() * 300) + 100,
            radius: PEG_RADIUS,
            hit: false,
            type: pegType
        });
    };

    return pegs;
}


export { 
    LevelOne,
    LevelTwo,
    LevelThree
};
